mapper.select:
SELECT DISTINCT IFNULL(T1.MODEL_NAME , '') AS MODEL_NAME /*모델명*/ FROM DC_CAR_MODEL T1
T1.MODEL_NAME = #{modelName}
, T1.MANUFACTURER_CODE = #{manufacturerCode}
AND T1.MODEL_NAME IS NOT NULL

mapper.update:
UPDATE DC_CAR_MODEL
MODEL_NAME = #{modelName} , MODEL_DETAIL_NAME = #{modelDetailName} , MANUFACTURER_CODE = #{manufacturerCode} , YEAR = #{year} , COUNTRY_CODE = #{countryCode} , IMPORT_CODE = #{importCode} , CARTYPE_CODE = #{cartypeCode} , FUEL_CODE = #{fuelCode} , TRANSMISSION_CODE = #{transmissionCode} , DRIVE_TYPE_CODE = #{driveTypeCode} , DRIVE_LICENSE_CODE = #{driveLicenseCode} , DISPLACEMENT = #{displacement} , MAXIMUM_PASSENGER = #{maximumPassenger} , DEL_YN = #{delYn} , MOD_ID = #{modId} , MOD_DT = NOW()
MD_IDX = #{mdIdx}

mapper.insert:

     INSERT INTO DC_CAR_MODEL (
         MODEL_NAME
      ,  MODEL_DETAIL_NAME
      ,  YEAR
      ,  MANUFACTURER_CODE
      ,  COUNTRY_CODE
      ,  CARTYPE_CODE
      ,  DEL_YN
      ,  REG_ID
      ,  REG_DT
   ) VALUES (
        #{modelName}
      ,     #{modelDetailName}
      ,     #{year}
      ,     #{manufacturerCode}
      ,     #{countryCode}
      ,     #{cartypeCode}
      ,   'N'
      ,     #{regId}
      ,   NOW()
   )


			SELECT LAST_INSERT_ID();
