mapper.select:
SELECT DISTINCT T1.MENU_CD AS MENU_CD /* 메뉴코드 */ , T1.MENU_NM AS MENU_NM /* 메뉴명 */ , T1.MENU_URL AS MENU_URL /* 메뉴경로 */ , T1.UPPER_MENU_CD AS UPPER_MENU_CD /* 부모번호 */ -- , LTRIM (GROUP_CONCAT (T1.MENU_NM separator ' > ')) AS DEPTH_FULLNAME /*전체 메뉴 경로*/ , IFNULL(T1.USE_YN, 'N') AS USE_YN /* 메뉴코드 사용여부 */ , T2.NEW_YN AS NEW_YN /* 등록권한여부 1 사용 0 미사용*/ , T2.READ_YN AS READ_YN /* 읽기권한여부 1 사용 0 미사용*/ , T2.DEL_YN AS DEL_YN /* 삭제권한여부 1 사용 0 미사용*/ , T2.EXCELDOWN_YN AS EXCELDOWN_YN /* 엑셀다운권한여부 1 사용 0 미사용*/ , T1.MENU_ORDER AS MENU_ORDER /* ORDER BY 순서*/ , T1.MENU_DC AS MENU_DC /* 메뉴 설명*/ , T1.MENU_LEVEL AS MENU_LEVEL /* 메뉴 LEVEL*/ FROM DC_MENU T1 LEFT JOIN DC_MENU_TEMPLATE T2 ON T1.MENU_CD = T2.MENU_CD LEFT JOIN DC_MENU_AUTH T3 ON T2.ROLE = T3.ROLE
T1.USE_YN='Y' AND T2.READ_YN='Y'
AND T3.ROLE=#{userRole}

SELECT IFNULL(TEMPLATE_CD , '') AS TEMPLATE_CD /*템플릿코드*/ , IFNULL(TEMPLATE_NM , '') AS TEMPLATE_NM /*템플릿명*/ , IFNULL(TEMPLATE_MEMO , '') AS TEMPLATE_MEMO /*템플릿설명*/ , IFNULL(REG_DT , '') AS REG_DT /*등록일*/ , IFNULL(REG_ID , '') AS REG_ID /*등록자*/ , IFNULL(MOD_DT , '') AS MOD_DT /*수정자*/ , IFNULL(MOD_ID , '') AS MOD_ID /*수정자*/ , IFNULL(ROLE , '') AS ROLE /**/ FROM DC_MENU_TEMPLATE
AND TEMPLATE_CD =#{templateCd}
AND TEMPLATE_NM= #{templateNm}

mapper.update:
UPDATE DC_QUESTION
QU_ANSWER = #{quAnswer} , QU_ANSWER_YN = #{quAnswerYn} , ANSWERER_ID = #{answererId} , ANSWER_DT = #{answerDt} , IMG_IDX = #{imgIdx} ,
QU_IDX = #{quIdx}

mapper.insert:

       INSERT INTO DC_NOTICE(
           NT_TITLE
         , NT_CONTENT
         , REG_ID
         , REG_DT
       )VALUES (
          #{ntTitle}
          , #{ntContent}
          , #{regId}
          , NOW()
      )


			SELECT LAST_INSERT_ID();

mapper.update:
UPDATE DC_NOTICE
NT_TITLE = #{ntTitle} , NT_CONTENT = #{ntContent} , MOD_ID = #{modId} , IMG_IDX = #{imgIdx} , MOD_DT = NOW()
NT_IDX = #{ntIdx}
