mapper.select:
SELECT IFNULL(A.UR_IDX , '' ) AS UR_IDX /* 회원IDX */ , IFNULL(A.USER_ID , '' ) AS USER_ID /* 회원ID */ , IFNULL(A.USER_PASSWORD , '' ) AS USER_PASSWORD /* 비밀번호 */ , IFNULL(A.USER_STATUS_CODE , '' ) AS USER_STATUS_CODE /* 회원상태코드 */ , IFNULL(A.USER_NAME , '' ) AS USER_NAME /* 이름 */ , IFNULL(A.USER_BIRTHDAY , '' ) AS USER_BIRTHDAY /* 생년월일 */ , IFNULL(A.USER_CONTACT1 , '' ) AS USER_CONTACT1 /* 연락처1 */ , IFNULL(A.USER_CONTACT2 , '' ) AS USER_CONTACT2 /* 연락처2 */ , IFNULL(A.USER_GENDER , '' ) AS USER_GENDER /* 성별CODE */ , IFNULL(A.USER_ZIPCODE , '' ) AS USER_ZIPCODE /* 우편번호 */ , IFNULL(A.USER_ADDRESS , '' ) AS USER_ADDRESS /* 주소 */ , IFNULL(A.USER_ADDRESS_DETAIL , '' ) AS USER_ADDRESS_DETAIL /* 상세주소 */ , IFNULL(A.USER_IDENTITY_AUTH_YN , '' ) AS USER_IDENTITY_AUTH_YN /* 본인인증여부 */ , IFNULL(A.USER_IDENTITY_AUTH_DATE , '' ) AS USER_IDENTITY_AUTH_DATE /* 인증일자 */ , IFNULL(A.USER_CI , '' ) AS USER_CI /* USER_CI */ , IFNULL(A.USER_DI , '' ) AS USER_DI /* USER_DI */ , IFNULL(A.USER_NATIONAL_CODE , '' ) AS USER_NATIONAL_CODE /* 내/외국인 구분 */ , IFNULL(A.USER_CERT_TYPE , '' ) AS USER_CERT_TYPE /* 인증서유형 */ , IFNULL(A.USER_GRADE_CODE , '' ) AS USER_GRADE_CODE /* 회원등급 */ , IFNULL(A.USER_ROLE , '' ) AS USER_ROLE /* 멤버역할(ROLE) */ , IFNULL(A.RT_IDX , '' ) AS RT_IDX /* 회원사DIX */ , IFNULL(A.USER_GROUP_CODE , '' ) AS USER_GROUP_CODE /* 회원분류코드 */ , IFNULL(A.USER_LICENSE_OWN_YN , '' ) AS USER_LICENSE_OWN_YN /* 면허소유여부 */ , IFNULL(A.USER_PAY_REGISTER_YN , '' ) AS USER_PAY_REGISTER_YN /* 결제수단등록여부 */ , IFNULL(A.CORPORATION_IDX , '' ) AS CORPORATION_IDX /* 법인IDX */ , IFNULL(A.USER_PUSH_AGREE_YN , '' ) AS USER_PUSH_AGREE_YN /* PUSH동의여부 */ , IFNULL(A.JOIN_CHANNEL , '' ) AS JOIN_CHANNEL /* 가입경로 */ , IFNULL(A.SOCIAL_LOGIN_PATH , '' ) AS SOCIAL_LOGIN_PATH /* 연동로그인경로 */ , IFNULL(A.SOCIAL_LOGIN_EMAIL , '' ) AS SOCIAL_LOGIN_EMAIL /* 연동로그인메일 */ , IFNULL(A.USER_WITHDRAW_DATE , '' ) AS USER_WITHDRAW_DATE /* 회원탈퇴일시 */ , IFNULL(A.LOGIN_FAIL_COUNT , 0 ) AS LOGIN_FAIL_COUNT /* 로그인시도횟수 */ , DATE_FORMAT(A.REG_DT , '%Y%m%d') AS REG_DT /* 등록일시 */ , IFNULL(A.REG_ID , '' ) AS REG_ID /* 등록자 */ , DATE_FORMAT(A.MOD_DT , '%Y%m%d') AS MOD_DT /* 수정일시 */ , IFNULL(A.MOD_ID , '' ) AS MOD_ID /* 수정자 */ , IFNULL(C.UL_IDX , '' ) AS UL_IDX /* 면허정보idx */ , IFNULL(C.LICENSE_NUMBER , '' ) AS LICENSE_NUMBER /* 면허번호 */ , IFNULL(C.LICENSE_CODE , '' ) AS LICENSE_CODE /* 면허코드 */ , IFNULL(C.LICENSE_LOCATION , '' ) AS LICENSE_LOCATION /* 면허지역 */ , IFNULL(C.LICENSE_IMG_NAME , '' ) AS LICENSE_IMG_NAME /* 면허사진 */ , IFNULL(C.LICENSE_NUMBER2 , '' ) AS LICENSE_NUMBER2 /* 면허번호2 */ , IFNULL(C.LICENSE_USER_BIRTHDAY , '' ) AS LICENSE_USER_BIRTHDAY /* 면허자 생년월일 */ , IFNULL(C.LICENSE_USER_NAME , '' ) AS LICENSE_USER_NAME /* 면허자 이름 */ , IFNULL(C.LICENSE_EXPIRATION , '' ) AS LICENSE_EXPIRATION /* 갱신기간 */ , IFNULL(C.LICENSE_ISSUE_DT , '' ) AS LICENSE_ISSUE_DT /* 발급일 */ , IFNULL(C.LICENSE_OWNER_NAME , '' ) AS LICENSE_OWNER_NAME /* 면허소유자명 */ , IFNULL(C.USE_YN , '' ) AS USE_YN /* DC_USER_LICENSE_INFO 사용여부 */ , (SELECT COUNT(*) reserveCnt FROM DC_RESERVE_MASTER WHERE UR_IDX = A.UR_IDX AND RESERVE_STATUS_CODE in ('RS', 'RO', 'RW', 'RS') ) AS RESERVE_CNT /*대여횟수*/ FROM DC_USER_INFO A LEFT OUTER JOIN DC_COMMON_CODE B ON B.RT_CODE='RL' AND A.USER_ROLE = B.CODE LEFT OUTER JOIN DC_USER_LICENSE_INFO C ON A.UR_IDX = C.UR_IDX
AND A.UR_IDX = #{urIdx}
AND A.USER_ID = #{userId}
AND A.USER_NAME = #{userName}
AND A.USER_BIRTHDAY = #{userBirthday}
AND A.USER_CONTACT1 = #{userContact1}
AND A.USER_CI = #{userCi}
AND A.USER_PASSWORD = #{userPassword}

mapper.update:
 UPDATE DC_USER_INFO 

        USER_ID				= #{userId}  			
      , USER_NAME				= #{userName}  			
      , JOIN_CHANNEL			= #{joinChannel}  		
      , USER_BIRTHDAY			= #{userBirthday}  		
      , USER_CONTACT1			= #{userContact1}  		
      , USER_GENDER			= #{userGender}  		
      , USER_ROLE				= #{userRole}  			
      , USER_STATUS_CODE		= #{userStatusCode} 	
      , USER_GRADE_CODE		= #{userGradeCode} 	    
      , USER_LICENSE_OWN_YN 	= #{userLicenseOwnYn}  	 	
      , MOD_ID 				= #{modId}			 	
      , USE_YN 				= #{useYn}			 	

, MOD_DT = NOW()


         UR_IDX = #{urIdx}

UPDATE DC_USER_LICENSE_INFO 

        LICENSE_NUMBER		= #{licenseNumber}  	
     ,  LICENSE_CODE			= #{licenseCode}  		
     ,  LICENSE_LOCATION		= #{licenseLocation}  	
     ,  LICENSE_EXPIRATION	= #{licenseExpiration}  
     ,  LICENSE_ISSUE_DT		= #{licenseIssueDt}  	
     ,  LICENSE_OWNER_NAME	= #{licenseOwnerName}   
     ,  LICENSE_IMG_NAME	= #{licenseImageName}   
     ,  LICENSE_NUMBER2	= #{licenseNumber2}   
     ,  LICENSE_USER_BIRTHDAY	= #{licenseUserBirthday}   
     ,  LICENSE_USER_NAME	= #{licenseUserName}   
     ,  MOD_ID 				= #{modId}				 	
   , MOD_DT = NOW()



        UR_IDX = #{urIdx}

mapper.insert:

     INSERT INTO DC_USER_LICENSE_INFO (
     	   UL_IDX
		,  UR_IDX
		,  LICENSE_NUMBER
		,  LICENSE_CODE
		,  LICENSE_LOCATION
		,  LICENSE_EXPIRATION
		,  LICENSE_ISSUE_DT
		,  LICENSE_OWNER_NAME
		,  LICENSE_NUMBER2
		,  LICENSE_USER_BIRTHDAY
		,  LICENSE_USER_NAME
		,  REG_DT
		,  REG_ID
	) VALUES (
			#{ulIdx}
		,	#{urIdx}
		,  	#{licenseNumber}
		,  	#{licenseCode}
		,  	#{licenseLocation}
		,  	#{licenseExpiration}
		,  	#{licenseIssueDt}
		,  	#{licenseOwnerName}
		,  	#{licenseNumber2}
		,  	#{licenseUserBirthday}
		,  	#{licenseUserName}
		,  	NOW()
		,  	#{regId}
	)


