mapper.select:
SELECT IFNULL(T1.CR_IDX, '') AS CR_IDX /*차량IDX */ , IFNULL(T1.RT_IDX, '') AS RT_IDX /*회사IDX */ , IFNULL(T1.REG_DT, '') AS REG_DT /*등록날짜 */ , IFNULL(T2.COMPANY_NAME, '') AS COMPANY_NAME /*회원사 이름 */ , IFNULL(T2.BRANCH_NAME, '') AS BRANCH_NAME /*지점 */ , IFNULL(T1.MODEL_NAME , '') AS MODEL_NAME /*모델명 */ , IFNULL(T1.MODEL_DETAIL_NAME , '') AS MODEL_DETAIL_NAME /*모델상세명 */ , IFNULL(T1.CAR_NUMBER , '') AS CAR_NUMBER /*차량번호 */ , IFNULL(T1.FUEL_CODE , '' ) AS FUEL_CODE /*연료구분code*/ -- , GET_COMMON_CODE_VALUE('CR', T1.FUEL_CODE) AS FUEL_NAME /* 연료명 */ , IFNULL(T1.MILEAGE, '0') AS MILEAGE /*누적KM - 주행거리*/ , IFNULL(T1.YEAR , '' ) AS YEAR /*년도 */ , IFNULL(T4.DAILY_STANDARD_PAY, '0') AS DAILY_STANDARD_PAY /* 일 기본요금*/ , IFNULL(T4.MONTHLY_STANDARD_PAY, '0') AS MONTHLY_STANDARD_PAY /* 월 기본요금*/ , CASE WHEN IFNULL(RESERVE_ABLE_YN, 'Y') = 'N' THEN '예약불가' WHEN ( SELECT COUNT(CR_IDX) FROM DC_RESERVE_MASTER WHERE CR_IDX = T1.CR_IDX AND RESERVE_STATUS_CODE = '예약' AND DATE(NOW()) >= DATE(DATE_FORMAT(CONCAT(RENT_START_DAY,' ', RENT_START_TIME), '%Y-%m-%d %H:%i:%s')) AND DATE(NOW()) <= DATE(DATE_FORMAT(CONCAT(RENT_END_DAY,' ', RENT_END_TIME), '%Y-%m-%d %H:%i:%s')) ) > 0 THEN '대여중' WHEN ( SELECT COUNT(CR_IDX) FROM DC_RESERVE_MASTER WHERE CR_IDX = T1.CR_IDX AND RESERVE_STATUS_CODE = '예약' AND DATE_FORMAT(IFNULL(NOW(), ''), '%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(RESERVE_DATE-(IFNULL(T3.RETURN_INSPECTION_TIME, 0)/24), '%Y-%m-%d %H:%i:%s') AND DATE_FORMAT(IFNULL(NOW(), ''), '%Y-%m-%d %H:%i:%s') < DATE_FORMAT(DATE_FORMAT(RENT_START_DAY||RENT_START_TIME, '%Y-%m-%d %H:%i:%s'), '%Y-%m-%d %H:%i:%s') ) > 0 THEN '예약중' WHEN ( SELECT COUNT(CR_IDX) FROM DC_RESERVE_MASTER WHERE CR_IDX = T1.CR_IDX AND RESERVE_STATUS_CODE = '예약' AND DATE_FORMAT(IFNULL(NOW(), ''), '%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(DATE_FORMAT(RENT_END_DAY||RENT_END_TIME, '%Y-%m-%d %H:%i:%s')+(IFNULL(T3.RETURN_INSPECTION_TIME, 0)/24), '%Y-%m-%d %H:%i:%s') AND DATE_FORMAT(IFNULL(NOW(), ''), '%Y-%m-%d %H:%i:%s') < DATE_FORMAT(DATE_FORMAT(RENT_END_DAY||RENT_END_TIME, '%Y-%m-%d %H:%i:%s'), '%Y-%m-%d %H:%i:%s') ) > 0 THEN '대기' ELSE '대기' END AS RESERVE_STATUS /* 상태 */ , IFNULL(T1.DAILY_YN, 'Y') AS DAILY_YN /* 일대여 사용유무 */ , IFNULL(T1.MONTHLY_YN, 'Y') AS MONTHLY_YN /* 월대여 사용유무 */ , (SELECT COUNT(HOL_IDX) FROM DC_HOLIDAYS WHERE CR_IDX = T1.CR_IDX AND HOLIDAY_END_DT >= DATE_FORMAT(NOW(), '%Y-%m-%d')) AS SUSPEND_CNT /* 휴차일 카운트 */ FROM DC_CAR_INFO T1 INNER JOIN DC_RENT_COMPANY T2 ON T1.RT_IDX = T2.RT_IDX LEFT JOIN DC_RENT_COMPANY_TIME T3 ON T1.RT_IDX = T3.RT_IDX LEFT OUTER JOIN DC_PAYMENT_INFO T4 ON T1.CR_IDX = T4.CR_IDX -- ) A
1=1
AND T1.RT_IDX = #{rtIdx}
AND A.RESERVE_STATUS = #{reserveStatus}

mapper.update:
UPDATE DC_CAR_INFO
IMG_IDX = #{imgIdx} , MANUFACTURER_CODE = #{manufacturerCode} , CARTYPE_CODE = #{cartypeCode}
CR_IDX = #{crIdx}

mapper.select:
SELECT IFNULL(T1.CR_IDX , '' ) AS CR_IDX /*차량idx*/ , IFNULL(T1.RT_IDX , '' ) AS RT_IDX /*제휴사idx*/ , IFNULL(T2.COMPANY_NAME , '' ) AS COMPANY_NAME /*회사명*/ , IFNULL(T2.BRANCH_NAME , '' ) AS BRANCH_NAME /*지점명*/ , IFNULL(T1.MD_IDX , '' ) AS MD_IDX /*모델idx*/ , IFNULL(T1.MODEL_NAME , '' ) AS MODEL_NAME /*모델명*/ , IFNULL(T1.MODEL_DETAIL_NAME , '' ) AS MODEL_DETAIL_NAME /*모델상세명*/ , IFNULL(T1.FUEL_CODE , '' ) AS FUEL_CODE /*연료구분code*/ , IFNULL(T1.CAR_REG_DT , '' ) AS CAR_REG_DT /*차량등록일*/ , IFNULL(T1.COLOR_NAME , '' ) AS COLOR_NAME /*색상*/ , IFNULL(T1.CAR_NUMBER , '' ) AS CAR_NUMBER /*차량번호*/ , IFNULL(T1.CAR_CHASSIS_NUMBER , '' ) AS CAR_CHASSIS_NUMBER /*차대번호*/ , IFNULL(T1.MILEAGE , '0') AS MILEAGE /*주행거리*/ , IFNULL(T1.RESERVE_ABLE_YN , '' ) AS RESERVE_ABLE_YN /*차량예약가능여부code*/ , IFNULL(T1.DEL_YN , '' ) AS DEL_YN /*차량삭제여부*/ , IFNULL(T1.YEAR , '' ) AS YEAR /*연식*/ /* 추가 입력 */ , IFNULL(T1.TRANSMISSION_CODE , '' ) AS TRANSMISSION_CODE /*변속기구분code*/ , IFNULL(T1.DRIVE_TYPE_CODE , '' ) AS DRIVE_TYPE_CODE /*구동방식구분code*/ , IFNULL(T1.CARTYPE_CODE , '' ) AS CARTYPE_CODE /*차종code*/ , IFNULL(T1.DRIVE_LICENSE_CODE , '' ) AS DRIVE_LICENSE_CODE /*면허구분code*/ , IFNULL(T1.MANUFACTURER_CODE , '' ) AS MANUFACTURER_CODE /*제조사code*/ , IFNULL(T1.DISPLACEMENT , '' ) AS DISPLACEMENT /*배기량*/ , IFNULL(T1.MAXIMUM_PASSENGER , '' ) AS MAXIMUM_PASSENGER /*승차인원*/ , IFNULL(T1.IMG_IDX , '' ) AS IMG_IDX /*이미지idx*/ , IFNULL(T1.CAR_DRIVE_LIMIT , '' ) AS CAR_DRIVE_LIMIT /*주행거리제한*/ , IFNULL(T1.AGE_LIMIT , '' ) AS AGE_LIMIT /*대여연령제한(장기,단기)*/ , IFNULL(T1.GARAGE_ADDR , '' ) AS GARAGE_ADDR /*차고지주소*/ , IFNULL(T1.CAR_ETC , '' ) AS CAR_ETC /*비고*/
/* 보험료 */ , IFNULL(T3.CI_IDX , '' ) AS CI_IDX /*보험정보idx*/ , IFNULL(T3.PERSONAL_COVER , '' ) AS PERSONAL_COVER /*대인보상금액*/ , IFNULL(T3.PROPERTY_DAMAGE_COVER , '' ) AS PROPERTY_DAMAGE_COVER /*대물보상금액*/ , IFNULL(T3.ONSELF_DAMAGE_COVER , '' ) AS ONSELF_DAMAGE_COVER /*자손보상금액*/ , IFNULL(T3.INSURANCE_COPAYMENT , '' ) AS INSURANCE_COPAYMENT /*고객부담금(보험료)*/ , IFNULL(T3.CAR_DAMAGE_COVER , '' ) AS CAR_DAMAGE_COVER /*자차보상금액(고객부담금)*/ , IFNULL(T3.INSURANCE_COPAYMENT2 , '' ) AS INSURANCE_COPAYMENT2 /*고객부담금2(보험료)*/ , IFNULL(T3.CAR_DAMAGE_COVER2 , '' ) AS CAR_DAMAGE_COVER2 /*자차보상금액2(고객부담금)*/ , IFNULL(T3.INSURANCE_COPAYMENT3 , '' ) AS INSURANCE_COPAYMENT3 /*고객부담금3(보험료)*/ , IFNULL(T3.CAR_DAMAGE_COVER3 , '' ) AS CAR_DAMAGE_COVER3 /*자차보상금액3(고객부담금)*/ , IFNULL(T3.INSURANCE_COPAYMENT4 , '' ) AS INSURANCE_COPAYMENT4 /*고객부담금4(보험료)*/ , IFNULL(T3.CAR_DAMAGE_COVER4 , '' ) AS CAR_DAMAGE_COVER4 /*자차보상금액4(고객부담금)*/ , IFNULL(T3.CAR_DAMAGE1_YN , 'Y') AS CAR_DAMAGE1_YN /*자차1사용유무*/ , IFNULL(T3.CAR_DAMAGE2_YN , 'Y') AS CAR_DAMAGE2_YN /*자차2사용유무*/ , IFNULL(T3.CAR_DAMAGE3_YN , 'Y') AS CAR_DAMAGE3_YN /*자차3사용유무*/ , IFNULL(T3.CAR_DAMAGE4_YN , 'Y') AS CAR_DAMAGE4_YN /*자차4사용유무*/ -- -- /* 기본 요금제 */ , IFNULL(T4.PY_IDX , '' ) AS PY_IDX /*요금idx*/ , IFNULL(T4.DAILY_STANDARD_PAY , '' ) AS DAILY_STANDARD_PAY /*일기본요금*/ , IFNULL(T4.DAILY_MAX_RATE , '' ) AS DAILY_MAX_RATE /*일대여최대할인율*/ , IFNULL(T4.MONTHLY_STANDARD_PAY , '' ) AS MONTHLY_STANDARD_PAY /*월기본요금*/ , IFNULL(T4.MONTHLY_MAX_RATE , '' ) AS MONTHLY_MAX_RATE /*월대여최대할인율*/ , IFNULL(T4.MONTH3_DEPOSIT , '' ) AS MONTH3_DEPOSIT /*3개월보증금*/ , IFNULL(T4.MONTH6_DEPOSIT , '' ) AS MONTH6_DEPOSIT /*6개월보증금*/ , IFNULL(T4.MONTH9_DEPOSIT , '' ) AS MONTH9_DEPOSIT /*9개월보증금*/ , IFNULL(T4.MONTH12_DEPOSIT , '' ) AS MONTH12_DEPOSIT /*12개월보증금*/ , IFNULL(T4.DELIVERY_STANDARD_PAY , '' ) AS DELIVERY_STANDARD_PAY /*배달기본요금*/ , IFNULL(T4.DELIVERY_ADD_PAY , '' ) AS DELIVERY_ADD_PAY /*배달10KM단위추가요금*/ , IFNULL(T4.DELIVERY_MAX_RATE , '' ) AS DELIVERY_MAX_RATE /*배달최대할인율*/ , IFNULL(T4.DAILY_YN , '' ) AS DAILY_YN /*일대여사용유무*/ , IFNULL(T4.MONTHLY_YN , '' ) AS MONTHLY_YN /*월대여사용유무*/ , IFNULL(T4.CLOSEDVEHICLE_YN , '' ) AS CLOSEDVEHICLE_YN /*휴차일사용유무*/ , IFNULL(T1.PER_IDX , '' ) AS PER_IDX /*기간요금idx*/ FROM DC_CAR_INFO T1 INNER JOIN DC_RENT_COMPANY T2 ON T1.RT_IDX = T2.RT_IDX
LEFT OUTER JOIN DC_CAR_INFO_INSURANCE T3 ON T1.CR_IDX = T3.CR_IDX
LEFT OUTER JOIN DC_PAYMENT_INFO T4 ON T1.CR_IDX = T4.CR_IDX
AND T1.RT_IDX = #{rtIdx}
AND T1.CR_IDX = #{crIdx}

SELECT IFNULL(T1.CR_IDX , '' ) AS CR_IDX /*차량idx*/ , IFNULL(T1.RT_IDX , '' ) AS RT_IDX /*제휴사idx*/ , IFNULL(T2.COMPANY_NAME , '' ) AS COMPANY_NAME /*회사명*/ , IFNULL(T2.BRANCH_NAME , '' ) AS BRANCH_NAME /*지점명*/ , IFNULL(T1.MD_IDX , '' ) AS MD_IDX /*모델idx*/ , IFNULL(T1.MODEL_NAME , '' ) AS MODEL_NAME /*모델명*/ , IFNULL(T1.MODEL_DETAIL_NAME , '' ) AS MODEL_DETAIL_NAME /*모델상세명*/ , IFNULL(T1.CARTYPE_CODE , '' ) AS CAR_TYPE_CODE /*차종*/ FROM DC_CAR_INFO T1 INNER JOIN DC_RENT_COMPANY T2 ON T1.RT_IDX = T2.RT_IDX
T1.DEL_YN='N'
AND T1.RT_IDX = #{rtIdx}
AND T1.CARTYPE_CODE = #{cartypeCode}

mapper.update:
UPDATE DC_CAR_INFO
,RT_IDX = #{rtIdx} , CAR_NUMBER = #{carNumber} , YEAR = #{year} , CAR_REG_DT = #{carRegDt} , MODEL_NAME = #{modelName} , MODEL_DETAIL_NAME = #{modelDetailName} , MD_IDX = #{mdIdx} , FUEL_CODE = #{fuelCode} , COLOR_NAME = #{colorName} , MILEAGE = #{mileage} , AGE_LIMIT = #{ageLimit} , DAILY_YN = #{dailyYn} , MONTHLY_YN = #{monthlyYn} , MOD_ID = #{modId} , MOD_DT = NOW()
CR_IDX = #{crIdx}

UPDATE DC_CAR_INFO_INSURANCE
RT_IDX = #{rtIdx} , ONSELF_DAMAGE_COVER = #{onselfDamageCover} , PERSONAL_COVER = #{personalCover} , PROPERTY_DAMAGE_COVER = #{propertyDamageCover} , CAR_DAMAGE_COVER = #{carDamageCover} , INSURANCE_COPAYMENT = #{insuranceCopayment} , CAR_DAMAGE_COVER2 = #{carDamageCover2} , INSURANCE_COPAYMENT2 = #{insuranceCopayment2} , CAR_DAMAGE_COVER3 = #{carDamageCover3} , INSURANCE_COPAYMENT3 = #{insuranceCopayment3} , CAR_DAMAGE_COVER4 = #{carDamageCover4} , INSURANCE_COPAYMENT4 = #{insuranceCopayment4} , CAR_DAMAGE1_YN = #{carDamage1Yn} , CAR_DAMAGE2_YN = #{carDamage2Yn} , CAR_DAMAGE3_YN = #{carDamage3Yn} , CAR_DAMAGE4_YN = #{carDamage4Yn}
