mapper.select:
SELECT * FROM ( SELECT RM_IDX /* 회원순번 */ , RT_IDX /* 회원사 순번 */ , RT_P_IDX /* 부모 회원사 */ , RESERVE_TYPE_CODE , CASE WHEN RENT_MI >= 365*24*60 THEN 'Y' WHEN RENT_MI >= 30*24*60 THEN 'M' WHEN RENT_MI >= 24*60 THEN 'D' ELSE 'T' END AS YMD /* 기간구분 */ , LAND_CODE /* 지역구분 */ , RESERVE_CHANNEL /* 예약채널구분 */ , RESERVE_DATE /* 예약일시 */ , RESERVE_STATUS_CODE /* 예약상태 코드 */ , IFNULL(RESERVE_STATUS_CODE, 'RSV') AS RESERVE_STATUS_NAME /* 예약상태 명 */ , USER_NAME /* 회원명 */ , USER_CONTACT1 /* 연락처 */ , MODEL_NAME /* 모델명 */ , MODEL_DETAIL_NAME /* 모델상세명 */ , CAR_NUMBER /* 차량번호 */ , IFNULL(FUEL_CODE, 'CR') AS FUEL_NAME /* 연료명 */ , RENT_START_DAY /* 대여일시 */ , RENT_END_DAY /* 반납일시 */ , PERIOD_DT /* 사용기간 */ , COMPANY_NAME /* 회원사명 */ , BRANCH_NAME /* 지점명 */ , DELIVERY_TYPE_NAME /* 대여방법 */ , PAYMENT_TOTAL_AMOUNT /* 총 금액 */ , PAYMENT_AMOUNT /* 결제금액 */ FROM ( SELECT A.RM_IDX /* 예약 순번 */ , C.RT_IDX /* 회원사 순번 */ , D.RT_P_IDX /* 부모 회원사 */ , RESERVE_TYPE_CODE AS RESERVE_TYPE_CODE /* 예약종류code */ , CASE WHEN IFNULL(A.RESERVE_STATUS_CODE, 'RS') = 'RS' AND NOW() > STR_TO_DATE(A.RENT_END_DAY || A.RENT_END_TIME, '%Y-%m-%d %H:%i:%s') THEN 'RW' WHEN IFNULL(A.RESERVE_STATUS_CODE, 'RS') = 'RS' AND NOW() BETWEEN STR_TO_DATE(A.RENT_START_DAY || A.RENT_START_TIME, '%Y-%m-%d %H:%i:%s') AND STR_TO_DATE(A.RENT_END_DAY || A.RENT_END_TIME, '%Y-%m-%d %H:%i:%s') THEN 'RO' ELSE IFNULL(A.RESERVE_STATUS_CODE, 'RS') END AS RESERVE_STATUS_CODE /* 상태 */ , IFNULL(A.RESERVE_CHANNEL, 'CS') AS RESERVE_CHANNEL /* 예약 경로 [CA:두차관리자, CS:웹,어플] */ , ROUND((STR_TO_DATE(A.RENT_END_DAY || A.RENT_END_TIME, '%Y-%m-%d %H:%i:%s') - STR_TO_DATE(A.RENT_START_DAY || A.RENT_START_TIME, '%Y-%m-%d %H:%i:%s'))*24*60) AS RENT_MI /* 총 예약 시간(분) */ , DATE_FORMAT(A.RESERVE_DATE, '%Y-%m-%d %H:%i:%s') AS RESERVE_DATE /* 예약일 */ , IFNULL(A.RESERVE_USER_NAME, '') AS USER_NAME /* 예약자명 */ , IFNULL(A.RESERVE_USER_CONTACT1, '') AS USER_CONTACT1 /* 예약자 전화번호 */ , IFNULL(C.MODEL_NAME, '') AS MODEL_NAME /* 차종명 */ , IFNULL(C.MODEL_DETAIL_NAME, '') AS MODEL_DETAIL_NAME /* 차종상세명 */ , IFNULL(C.CAR_NUMBER, '') AS CAR_NUMBER /* 차량번호 */ , IFNULL(C.FUEL_CODE, '') AS FUEL_CODE /* 연료코드 */ , DATE_FORMAT(STR_TO_DATE(CONCAT(RENT_START_DAY,' ', RENT_START_TIME), '%Y-%m-%d %H:%i'), '%Y-%m-%d %H:%i') AS RENT_START_DAY /* 대여 시작 일시 */ , DATE_FORMAT(STR_TO_DATE(CONCAT(RENT_END_DAY,' ', RENT_END_TIME), '%Y-%m-%d %H:%i'), '%Y-%m-%d %H:%i') AS RENT_END_DAY /* 대여 종료 일시 */ , IFNULL(A.PERIOD_DT, '') AS PERIOD_DT /* 대여기간 */ , IFNULL(D.COMPANY_NAME , '') AS COMPANY_NAME /* 회원사명 */ , IFNULL(D.BRANCH_NAME, '') AS BRANCH_NAME /* 지점명 */ , IFNULL(A.DELIVERY_TYPE_CODE, '') AS DELIVERY_TYPE_CODE /* 대여방법 코드 */ , IFNULL(A.DELIVERY_TYPE_CODE, '') AS DELIVERY_TYPE_NAME /* 대여 방법 명 */ , PAYMENT_TOTAL_AMOUNT as PAYMENT_TOTAL_AMOUNT , (SELECT SUM(IFNULL(PAYMENT_AMOUNT, '0')) FROM DC_PAYMENT_DETAIL WHERE RM_IDX = A.RM_IDX) AS PAYMENT_AMOUNT /* 총 결제금액 */ , CASE WHEN INSTR(D.COMPANY_AddRESS||D.COMPANY_AddRESS_DETAIL, '제주') > 0 THEN 'JJ' ELSE 'IL' END AS LAND_CODE /* 내륙/제주 구분 [JJ:제주,IL:내륙,IS:제주 제외한 섬] */ FROM DC_RESERVE_MASTER A INNER JOIN DC_CAR_INFO C ON A.CR_IDX = C.CR_IDX INNER JOIN DC_RENT_COMPANY D ON A.RT_IDX = D.RT_IDX
AND D.RT_IDX = #{rtIdx}
AND D.RT_P_IDX = #{rtPIdx}
) X ) X2 ORDER BY RESERVE_DATE DESC

