mapper.select:
SELECT RT_IDX , IFNULL(RT_P_IDX , '' ) AS RT_P_IDX , IFNULL(COMPANY_NAME , '' ) AS COMPANY_NAME , IFNULL(COMPANY_ZIPCODE , '' ) AS COMPANY_ZIPCODE , IFNULL(COMPANY_ADDRESS , '' ) AS COMPANY_ADDRESS , IFNULL(COMPANY_ADDRESS_DETAIL , '' ) AS COMPANY_ADDRESS_DETAIL , IFNULL(LAT , '' ) AS LAT , IFNULL(LNG , '' ) AS LNG , IFNULL(ESTABLISHED_DATE , '' ) AS ESTABLISHED_DATE , IFNULL(COMPANY_REGISTRATION_NUMBER , '' ) AS COMPANY_REGISTRATION_NUMBER , IFNULL(COMPANY_REGISTRATION_IMG , '' ) AS COMPANY_REGISTRATION_IMG , IFNULL(ACCOUNT_BANK , '' ) AS ACCOUNT_BANK , IFNULL(ACCOUNT_NUMBER , '' ) AS ACCOUNT_NUMBER , IFNULL(ACCOUNT_HOLDER , '' ) AS ACCOUNT_HOLDER , IFNULL(ACCOUNT_IMG_IDX , '' ) AS ACCOUNT_IMG_IDX , IFNULL(LONGTERM_RENT_YN , '' ) AS LONGTERM_RENT_YN , IFNULL(SHORTTERM_RENT_YN , '' ) AS SHORTTERM_RENT_YN , IFNULL(ALLIANCE_STATUS , '' ) AS ALLIANCE_STATUS , IFNULL(BRANCH_ABLE_YN , '' ) AS BRANCH_ABLE_YN , IFNULL(CAR_COUNT , '' ) AS CAR_COUNT , IFNULL(ETC , '' ) AS ETC , IFNULL(REG_ID , '' ) AS REG_ID , IFNULL(REG_DT , '' ) AS REG_DT , IFNULL(MOD_ID , '' ) AS MOD_ID , IFNULL(MOD_DT , '' ) AS MOD_DT , IFNULL(DEL_YN , '' ) AS DEL_YN , IFNULL(COMPANY_CONTACT1 , '' ) AS COMPANY_CONTACT1 , IFNULL(COMPANY_CONTACT2 , '' ) AS COMPANY_CONTACT2 , IFNULL(ALARM_YN , '' ) AS ALARM_YN , IFNULL(COMPANY_REGISTRATION_NAME , '' ) AS COMPANY_REGISTRATION_NAME , IFNULL(BRANCH_NAME , '' ) AS BRANCH_NAME , IFNULL(ACCESS_YN , '' ) AS ACCESS_YN FROM DC_RENT_COMPANY
AND RT_IDX = #{rtIdx}
AND RT_P_IDX = #{rtPIdx}
ORDER BY COMPANY_NAME
